import React from 'react'
import styled from 'styled-components'
import rgba from './bensLibrary'

const ThemeToggler_ = styled.button(props => ({
  padding: '.5rem 2rem',
  borderRadius: '100px',
  outline: 0,
  border: 0,
  margin: '0 1rem',
  fontWeight: props.theme.weight.regular,
  background: props.theme.complement,
  transition: 'box-shadow .5s ease',
  display: 'block',
  margin: 'auto',

  '&:hover': {
    boxShadow: props.theme.mode === 'dark' && `0 10 20px 3px ${rgba(props.theme.complement, .3)}`,
    border: '10px solid white'
  }
}))

export default function ThemeToggler (props) {
  return (
    <ThemeToggler_ onClick={props.toggleMode} />

    //
    //
    // TODO: figure out how to apply an animation on click. Having trouble referring to the 
    // button in order to add styles to it
    //
    //
  )
}
