import React from 'react'
import styled from 'styled-components'

const Sketches_ = styled.div`
  width: 100%;
  display: grid;
  grid-gap: 2vw;
  grid-template-columns: repeat(3, 1fr);
`

const Sketch = styled.div`
  height: 300px;
  background: #111;
`

const SketchPreview = styled.div`
  width: 100%;
  height: 0;
  opacity: 0;
  background: ${props => props.theme.primary};
  border-radius: .25rem;
  transition: .5s ease;

  &.active {
    opacity: 1;
    height: ${window.innerWidth * .8 * (9/16)}px;
  }
`

export default class Sketches extends React.Component {
  constructor(props) {
    super(props)
    this.state = { previewOpen: false, currentSketch: null }
  }

  showSketch(sketch) {
    if (!this.state.previewOpen) {
      document.querySelector('#sketchPreview').classList.add('active')
      this.state.previewOpen = true
    }
    // do stuff
  }

  render() {
    return (
      <>
        <button onClick={() => this.showSketch('cock')}>Press me fag</button>
        <Sketches_>
          <Sketch></Sketch>
          <Sketch></Sketch>
          <Sketch></Sketch>
        </Sketches_>

        <SketchPreview id='sketchPreview' />
      </>
    )
  }
}
