export default function follower(p) {
  let win = { x: window.innerWidth, y: document.innerHeight }
  console.log(win)
  p.setup = () => {
    p.createCanvas(win.x, win.y)
    p.background(255,0,0)
  }

  p.draw = () => {
    p.background(0, 255, 0)
  }
}
