import React from 'react'
import styled from 'styled-components'
import breakpoint from 'styled-components-breakpoint'

const Gallery = styled.div`
  display: grid;
  border: 1pc solid red;
  grid-template-areas: 'mesh' 'matrix light';
  grid-gap: 5px;
`

const Project = styled.div`
  grid-area: ${props => props.id};
  background: 'green';
`

const projectData = [
  { id: 'mesh', name: 'Particle Mesh', description: '', repo: '', demo: '', img: '' },
  { id: 'matrix', name: 'Raining Code', description: '', repo: '', demo: '', img: '' },
  { id: 'light', name: 'Lightning', description: '', repo: '', demo: '', img: '' },
]


const projects = projectData.map((proj, i) => {
  return (
    <Project gridArea={proj.id}>

    </Project>
  )
})

export default function Programming (props) {
  return (
    <Gallery>
      {projects}
    </Gallery>
  )
}

