import React from 'react'
import styled from 'styled-components'

const ThemeToggler_ = styled.button(props => ({
  padding: '.5rem 2rem',
  borderRadius: '100px',
  outline: 0,
  margin: '0 1rem',
  fontWeight: props.theme.weight.regular,
  border: `2px solid ${props.theme.complement}`,
  background: props.theme.base,
  color: props.theme.complement,
  boxShadow: props.mode === 'dark' ? '0 0 50px 50px red' : ''
}))

export default function ThemeToggler (props) {
  return (
    <div style={{
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    }}>

      <ThemeToggler_ onClick={props.toggleMode} className={props.className} />
    </div>
  )
}
