class Particle {
  constructor(p) {
    this.respawn()
    this.pos = p.createVector(p.random(-100, p.width+100), p.random(-100, p.height+100))
    this.p = p
  }

  move() {
    this.pos.add(this.vel)
    this.checkPos()
  }

  draw() {
    this.p.strokeWeight(this.size)
    this.p.stroke(255)
    this.p.point(this.pos.x, this.pos.y)
  }

  applyForce(force) {
    let dist = force.mag()
    let strength = .3 / Math.pow(dist, 2)
    strength = p.constrain(strength, 0, .0005)
    force.setMag(strength)
    this.vel.add(force)
  }

  checkPos() {
    let x = this.pos.x
    let y = this.pos.y
    let s = 100

    if ((x < 0 - s) ||
        (x > this.p.width + s) ||
        (y < 0 - s) ||
        (y > this.p.height + s))
    {
        this.respawn();
    }
  }

  respawn() {
    let rate = this.p.random(.2, .4)
    this.pos = this.p.createVector(this.random(-100, this.p.width + 100), -100)
    this.vel = this.p.createVector(this.random(-rate, rate), this.p.random(0, rate))
    this.size = this.p.random(1, 10)
  }
}
