const sketchData = [
  { name: 'Particle Mesh', sketch: particleMesh, icon: 'nodes', description: 'One of my first complex sketches. Nodes attract to the cursor.' },
  { name: 'Lightning', sketch: 'b', icon: 'lightning', description: '...' },
  { name: 'Raining Katakana', sketch: 'c', icon: 'katakana', description: 'Recreation of the raining code animation from the Matrix' },
  { name: 'Orbit?', sketch: 'd', icon: 'orbit', description: '...' },
  { name: 'Light Speed', sketch: 'e', icon: 'starfield', description: 'Animation resembling the light-speed effect from Star Wars.' }
]

export default props => {
  const { theme } = props
  const [sketch, setSketch] = useState(null)

  const handleClick = clicked => {
    if (sketch && sketch.name === clicked.name) {
      setSketch(null)
    } else {
      setSketch(clicked)
    }
  }

  const suffix = props.theme.mode === 'dark' ? 'b' : 'w'
  return (
    <Root>
      <Div flex>
        {sketchData.map((s, i) => {
          const selected = (sketch && sketch.name) === s.name

          return (
            <Div w={'100%'} h={80} minW={80} mr={5} className='sketch' rounded center key={i} bg={theme[selected ? 'primary' : 'complement']} onClick={_ => handleClick(s)}>
              <img height={40} style={{ transform: selected && 'scale(1.2)' }} src={require(`../img/sketches/${s.icon}-${selected ? 'w' : suffix}.png`)} />
            </Div>
          )
        })}
      </Div>

      <AnimateHeight height={sketch ? 'auto' : 0}>
        <Text bold accent type='h3' mt={30} mb={10}>{sketch && sketch.name} <Text inline light ml={5}>{sketch && sketch.description}</Text></Text>
        <P5Wrapper sketch={sketch && sketch.sketch} theme={theme} />
        {/* <Div bg={theme.complement} center w={'100%'} p={50}>
            HEY BRO
        </Div> */}
      </AnimateHeight>
    </Root>
  )
}


