import React from 'react'
import styled from 'styled-components'
import rgba from './bensLibrary'

const ThemeToggler_ = styled.button(props => ({
  padding: '.5rem 2rem',
  borderRadius: '100px',
  outline: 0,
  border: 0,
  margin: '0 1rem',
  fontWeight: props.theme.weight.regular,
  background: props.theme.complement,
  transition: 'box-shadow .25s ease',
  display: 'block',
  margin: 'auto',

  '&:hover': {
    boxShadow: props.theme.mode === 'dark' && `0 0 20px 3px ${rgba(props.theme.complement, .3)}`
  }
}))

export default function ThemeToggler (props) {
  return (
    <ThemeToggler_ onClick={() => {
      props.toggleMode
      ThemeToggler_.style.animation = 'pulse 1s ease'
    }} />
  )
}
