import React from 'react'
import styled from 'styled-components'

const Sketches_ = styled.div`
  width: 100%;
  display: flex;
  justify-content: space-between;
  border: 1px solid red;
`

const Sketch = styled.div`
  width: 300px;
  height: 300px;
  background: #111;
`

const SketchPreview = styled.div`
  width: 100%;
  height: 0;
  opacity: 0;

  background: red;

  &:active {
    opacity: 1;
    border: 10px solid blue;
    height: ${window.innerWidth * .8 * (9/16)}
  }
`

export default class Sketches extends React.Component {
  constructor(props) {
    super(props)
    this.state = { previewOpen: false, currentSketch: null }
  }

  showSketch(sketch) {
    if (!this.state.previewOpen) {
      $('#sketchPreview').classList.add('active')
      this.state.previewOpen = true
      alert(sketch)
    }
    // do stuff
  }

  $(query) { return document.querySelector(query) }

  render() {
    return (
      <>
        <button onClick={() => showSketch('cock')}>Press me fag</button>
        <Sketches_>
          <Sketch></Sketch>
          <Sketch></Sketch>
          <Sketch></Sketch>
        </Sketches_>

        <SketchPreview id='sketchPreview' />
      </>
    )
  }
}
