const baseStyles = {
  primary: '#60b389',
  accent: '#97cfb3',

  weight: {
    thin: '100',
    regular: '400',
    bold: '700'
  },

  fontSize: {
    h1: '3rem',
    h2: '2rem',
    p: '1rem'
  }
}

export const theme = {
  dark: {
    mode: 'dark',
    base: '#111111',
    complement: '#eeeeee',
    ...baseStyles
  },

  light: {
    mode: 'light',
    base: '#eeeeee',
    complement: '#111111',
    ...baseStyles
  }
}
