export default function follower(p) {
  let win = { x: window.innerWidth, y: window.innerHeight }
  let cnv
  let bg
  let follower

  p.setup = () => {
    cnv = p.createCanvas(win.x, win.y)
    cnv.position(0,0)
    // cnv.style('z-index', -100)
    follower = new Follower(p.createVector(p.random(p.width), p.random(p.height)))
  }

  p.draw = () => {
    p.background(bg)
    follower.follow(p.mouseX, p.mouseY)
    follower.draw()
  }

  p.myCustomRedrawAccordingToNewPropsHandler = props => {
    bg = props.theme.base
  }

  window.onresize = () => {
    win = { x: window.innerWidth, y: window.innerHeight }
    p.resizeCanvas(win.x, win.y)
  }

  class Follower {
    constructor(pos) {
      this.pos = pos
      this.vel = p.createVector()
    }

    follow(mouse) {
      let dir = mouse.sub(this.pos).mult(0.0001)
      this.vel.add(dir)
    }

    draw() {
      p.fill('green')
      this.pos.add(this.vel)
      p.ellipse(this.pos.x, this.pos.y, 10)
      console.log(this.pos.x, this.pos.y)
    }

    // a vector pointing from v2 to v1
    sub(v1, v2) {
      return p.createVector(v2.x - v1.x, v2.y - v1.y)
    }
  }
}
