import React from 'react'
import styled from 'styled-components'
import rgba from './bensLibrary'

const ThemeToggler_ = styled.button(props => ({
  padding: '.5rem 2rem',
  borderRadius: '100px',
  outline: 0,
  border: 0,
  margin: '0 1rem',
  fontWeight: props.theme.weight.regular,
  border: `1px solid ${props.theme.complement}`,
  background: 'rgba(0,0,0,0)',
  boxShadow: props.theme.mode === 'dark' && `0 0 20px 3px ${rgba(props.theme.complement, .3)}`
  // boxShadow: '0 0 10px 5px rgba(255, 255, 255, .1)'
}))

export default function ThemeToggler (props) {
  return (
    <div style={{
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    }}>

      <ThemeToggler_ onClick={props.toggleMode} />
    </div>
  )
}
