export default function sketch(p) {
  let canvas  
  let grid
  let tileSize

  p.setup = () => {
    canvas = p.createCanvas(p.windowWidth, p.windowHeight)
    p.background('#0d1e35')
    tileSize = p.width / 50
    p.rectMode(p.CENTER)

    grid = new Grid(tileSize)
  }

  p.draw = () => {
    // p.clear()
    p.background('#0d1e35')
    // grid.forEach(col => {
    //   col.forEach(cell => {
    //     cell.draw()
    //   })
    // })
    grid.update()
  }

  class Grid {
    constructor(tileSize) {
      this.tileSize = tileSize
      this.grid = []
      for (let x = 0; x < p.width; x += tileSize) {
        let col = []
        for (let y = 0; y < p.height; y += tileSize) {
          col.push(new Cell(x, y, tileSize))
        }
        this.grid.push(col)
      }
    }

    update() {
      this.grid.forEach(col => {
        col.forEach(cell => {
          cell.draw()
        })
        // this.grid[curCol][curRow].highlight()
      })
      let curRow = Math.floor(p.mouseY / this.tileSize)
      let curCol = Math.floor(p.mouseX / this.tileSize)
      this.grid[curCol][curRow].highlight()
      // console.log(p.mouseX / this.tileSize, p.mouseY)
    }
  }

  class Cell {
    constructor(x, y, tileSize) {
      this.x = x
      this.y = y
      this.tileSize = tileSize
      this.brightness = 0
      this.randDist = p.random(500)
    }

    draw() {
      p.noStroke()
      // p.fill(255, this.brightness)
      p.fill(255)
      let mouse = p.createVector(p.mouseX, p.mouseY) 
      let pos = p.createVector(this.x, this.y)
      let cellSize = this.tileSize * (10 / (pos.dist(mouse)))
      if (cellSize > tileSize/4) cellSize = tileSize/4
      p.ellipse(this.x, this.y, cellSize)
      p.strokeWeight(cellSize/10)
      p.stroke('#fff')
      if (pos.dist(mouse) < this.randDist) p.line(mouse.x, mouse.y, pos.x, pos.y)
      if (this.brightness > 0) this.brightness--
    }

    highlight() {
      // p.fill('#fff')
      // p.noStroke()
      // p.rect(this.x, this.y, this.tileSize)
      this.brightness+=5
    }
  }

  // p.myCustomRedrawAccordingToNewPropsHandler = (newProps) => {
  //   if (canvas) {
  //     p.fill(newProps.color)
  //   }
  // }
}



