import React from 'react'
import styled from 'styled-components'

const Sketches_ = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
  border: 1px solid red;
`

const Sketch = styled.div`
  width: 300px;
  height: 300px;
  background: #111;
`

const SketchPreview = styled.div`
  width: 100%;
  height: 0;
  opacity: 0;

  background: red;

  &:active {
    opacity: 1;
    border: 10px solid blue;
    height: ${window.innerWidth * .8 * (9/16)}
  }
`

export default function Sketches (props) {
  return (
    <>
      <button onClick={() => document.querySelector('#sketchPreview').classList.add('active')}>Press me fag</button>
      <Sketches_>
        <Sketch></Sketch>
        <Sketch></Sketch>
        <Sketch></Sketch>
      </Sketches_>

      <SketchPreview id='sketchPreview' />
    </>
  )
}
