import React from 'react'
import styled from 'styled-components'

const ThemeToggler_ = styled.button`
  padding: .5rem 2rem;
  font-weight: ${props => props.theme.weight.regular};
  border: 2px solid ${props => props.theme.complement};
  border-radius: 100px;
  outline: 0;
  margin: 0 1rem;
  background: ${props => props.theme.base};
  color: ${props => props.theme.complement}
`

export default function ThemeToggler (props) {
  return (
    <div style={{
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    }}>

      <ThemeToggler_ onClick={props.toggleMode} className={props.className} />
    </div>
  )
}
