import React from 'react'
import styled from 'styled-components'
import hexToRgba from './bensLibrary'

const ThemeToggler_ = styled.button(props => ({
  padding: '.5rem 2rem',
  borderRadius: '100px',
  outline: 0,
  margin: '0 1rem',
  fontWeight: props.theme.weight.regular,
  background: props.theme.complement,
  boxShadow: `0 0 20px 50px ${hexToRgba(props.theme.accent), 1}`
  // boxShadow: '0 0 10px 5px rgba(255, 255, 255, .1)'
}))

export default function ThemeToggler (props) {
  return (
    <div style={{
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    }}>

      <ThemeToggler_ onClick={props.toggleMode} />
    </div>
  )
}
