export default function sketch(p) {
  let canvas  
  let grid
  let cellSize

  p.setup = () => {
    canvas = p.createCanvas(p.windowWidth, p.windowHeight*2)
    p.background('#0d1e35')
    cellSize = p.width / 50

    grid = new Grid(cellSize)
  }

  p.draw = () => {
    p.background('#0d1e35')
    grid.update()
  }

  class Grid {
    constructor(cellSize) {
      this.cellSize = cellSize
      this.grid = []
      for (let x = 0; x < p.width; x += cellSize) {
        let col = []
        for (let y = 0; y < p.height; y += cellSize) {
          col.push(new Cell(p.createVector(x + cellSize/2, y + cellSize/2), cellSize))
        }
        this.grid.push(col)
      }
    }

    update() {
      this.grid.forEach(col => {
        col.forEach(cell => {
          cell.draw()
        })
      })
    }
  }

  class Cell {
    constructor(pos, cellSize) {
      this.pos = pos
      this.cellSize = cellSize
    }

    draw() {
      let mouse = p.createVector(p.mouseX, p.mouseY) 
      let mDist = mouse.dist(this.pos)
      let scaledSize = this.cellSize * (5 / mDist)

      if (scaledSize > this.cellSize/4) scaledSize = cellSize/4

      p.strokeWeight(scaledSize/10)
      p.stroke(255)
      p.ellipse(this.pos.x, this.pos.y, scaledSize)
    }
  }

  p.myCustomRedrawAccordingToNewPropsHandler = (newProps) => {
    // if (canvas) {
    //   p.fill(newProps.color)
    // }
  }
}
