  @media only screen and (max-width: 700px) { grid-template-columns: repeat(2, 1fr); }
`
const Sketch = styled.div`
  height: 100px;
  background: ${props => props.theme.complement};
  border-radius: .25rem;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 10;

  &:hover {
    div {
      transform: scale(1.2);
    }
  }
`
const Icon = styled.div`
  width: 100px;
  /* height: 100px; */
  background-image: url('${props => require('../img/sketches/' + props.name + (props.theme.mode === 'dark' ? '-b' : '-w') + '.png')}');
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  transition: all .5s ease;
`
