import React from 'react'
import ReactDOM from 'react-dom'

import { ThemeProvider, createGlobalStyle } from 'styled-components'

import { theme } from './theme'
import BenWeber from './benWeber'

// this and theme should be put into styles.js or something. Really want to keep styles and content separate
const GlobalStyle = createGlobalStyle`
  body {
    background: ${props => props.theme.base}
  }
  * {
    font-family: 'Heebo';
    transition: background .5s ease;
    font-size: 1rem;
    box-sizing: border-box;
  }
  p {
    color: pink
  }
  @keyframes pulse {
    0% { box-shadow: 0 0 0 0 rgba(255, 255, 255, .15) }
    100% { box-shadow: 0 0 0 60px rgba(255, 255, 255, 0) }
  }
`

class App extends React.Component {
  state = {
    mode: 'dark'
  }

  toggleMode = () => {
    this.state.mode === 'dark' ? this.setState({ mode: 'light' })
      : this.setState({ mode: 'dark' })
  }

  render() {
    return (
      <ThemeProvider theme={theme[this.state.mode]} style={{ border: '5px dashed pink' }}>
        <GlobalStyle />
        <BenWeber toggleMode={this.toggleMode} />
      </ThemeProvider>
    )
  }  
}

ReactDOM.render(
  <App />,
  document.getElementById('root')
)
