import React from 'react'
import ReactDOM from 'react-dom'
import styled, { injectGlobal, ThemeProvider } from 'styled-components'

// import FuckyButton from './fuckyButton'
import theme from './theme'
alert(theme)
const darkMode = theme.dark
const lightMode = theme.light

// injectGlobal`
//   body {
//     background-color: papayawhip;
//   }

//   * {
//     margin: 0;
//     padding: 0;
//   }
// `

// const localStyles = theme => {
//   background: theme.
// }

const LittleComponent = styled.div`
  width: 200px;
  height: 50px;
  border-radius: 25px;
  background: palevioletred;
  border: 5px solid white;
  color: ${props => props.theme.primary}
`

class BenWeber extends React.Component {
  state = {
    theme: darkMode
  }

  toggleMode = () => {
    this.state.theme === darkMode ? this.setState({ theme: lightMode }) 
    : this.setState({ theme: darkMode })
  }

  render() {
    console.log(this.state)
    return (

      <ThemeProvider theme={this.state.theme}>
        <LittleComponent>
          Primary
        </LittleComponent>
      </ThemeProvider>
    )
  }  
}

//use withStyles
ReactDOM.render(<BenWeber />, document.getElementById('root'))
