export default function sketch(p) {
  let canvas
  let nodes = []

  p.setup = () => {
    canvas = p.createCanvas(p.windowWidth, p.windowHeight)
    p.background('#0d1e35')

    for (let i = 0; i < 10; i++) {
      nodes.push(new Node(p, p.createVector(p.random(p.width), p.random(p.height))))
    }
  }

  p.draw = () => {
    p.clear()
    p.background('#0d1e35')
    nodes.forEach(node => {
      node.update()

      let nearbyNodes = []
      nodes.forEach(other => {
        if (other !== node && node.pos.dist(other.pos) <= 500) {
          nearbyNodes.push(other)
        }
      })
      node.neighbors = nearbyNodes
    })
  }

  p.myCustomRedrawAccordingToNewPropsHandler = (newProps) => {
    if (canvas) {
      p.fill(newProps.color)
    }
  }
}

class Node {

  constructor(p, origin, maxDist) {
    this.p = p
    this.neighbors = []
    this.r = p.random(3, 10)
    this.rate = p.random(.5, 4)
    this.respawn()
  }

  respawn() {
    this.pos = this.p.createVector(this.p.random(this.p.width), this.p.random(-300, -50))
    this.vel = this.p.createVector(this.p.random(-this.rate/2, this.rate/2), this.p.random(this.rate))
  }

  update() {
    this.pos.add(this.vel)

    this.interract(this.neighbors)

    // draw
    this.p.stroke('#fff')
    this.p.strokeWeight(this.r)
    this.p.point(this.pos.x, this.pos.y)

    this.checkBounds()
  }

  interract(neighbors) {
    neighbors.forEach(other => {
      this.p.strokeWeight(100 / this.pos.dist(other.pos))
      this.p.stroke('#fff')
      this.p.line(this.pos.x, this.pos.y, other.pos.x, other.pos.y)
    })
  }

  checkBounds() {

  }
}
