import React from 'react'
import styled from 'styled-components'
import rgba from './bensLibrary'

const ThemeToggler_ = styled.button(props => ({
  opacity: .3,
  padding: '.5rem 2rem',
  borderRadius: '100px',
  outline: 0,
  border: 0,
  margin: '0 1rem',
  fontWeight: props.theme.weight.regular,
  background: props.theme.complement,
  transition: 'all .5s ease',
  display: 'block',
  margin: 'auto',

  '&:hover': {
    boxShadow: props.theme.mode === 'dark' && `0210 20px 3px ${rgba(props.theme.complement, 1)}`,
    opacity: 1,
    // transform: 'scaleX(1.1)',
    padding: '.5rem 3rem'
  }
}))

let used = false;

export default function ThemeToggler (props) {
  if (!used) {
    used = true
    return <ThemeToggler_ onClick={props.toggleMode} used={false} />
  } else {
    return <ThemeToggler_ onCLick={props.toggleMode} used={true} />
  }


