import React from 'react'
import styled from 'styled-components'
import rgba from './bensLibrary'

const ThemeToggler_ = styled.button(props => ({
  padding: '.5rem 2rem',
  borderRadius: '100px',
  outline: 0,
  margin: '0 1rem',
  fontWeight: props.theme.weight.regular,
  border: `2px solid ${props.theme.complement}`,
  background: props.theme.base,
  color: props.theme.complement,
  boxShadow: props.theme.mode === 'dark' && `0 0 20px 5px ${rgba(props.theme.complement), 100}`
}))

alert(props.theme.complement)

export default function ThemeToggler (props) {
  return (
    <div style={{
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    }}>

      <ThemeToggler_ onClick={props.toggleMode} className={props.className} />
    </div>
  )
}
