import React from 'react'
import ReactDOM from 'react-dom'
import styled, { createGlobalStyle, ThemeProvider } from 'styled-components'

import { theme } from './theme'
import FuckyButton from './fuckyButton'

const darkMode = theme.dark
const lightMode = theme.light

const GlobalStyle = createGlobalStyle`
  * { transition: all .5s ease }  
  body { background: ${props => props.theme.base} }
`

class BenWeber extends React.Component {
  state = {
    theme: darkMode
  }

  toggleMode = () => {
    this.state.theme === darkMode ? this.setState({ theme: lightMode }) 
    : this.setState({ theme: darkMode })
  }

  render() {
    console.log(this.state)
    return (

      <ThemeProvider theme={this.state.theme}>
        <GlobalStyle />

        <FuckyButton onClick={this.toggleMode} />

      </ThemeProvider>
    )
  }  
}

//use withStyles
ReactDOM.render(<BenWeber />, document.getElementById('root'))
