export default function follower(p) {
  let win = { x: window.innerWidth, y: window.innerHeight }
  let cnv
  let bg
  let follower

  p.setup = () => {
    cnv = p.createCanvas(win.x, win.y)
    cnv.position(0,0)
    cnv.style('z-index', -100)
    follower = new Follower(p.random(p.width), p.random(p.height))
  }

  p.draw = () => {
    p.background(bg)
    follower.follow(p.createVector(p.mouseX, p.mouseY))
    follower.draw()
  }

  p.myCustomRedrawAccordingToNewPropsHandler = props => {
    console.log(props)
    bg = props.theme.base
  }

  window.onresize = () => {
    win = { x: window.innerWidth, y: window.innerHeight }
    p.resizeCanvas(win.x, win.y)
  }

  class Follower {
    constructor(pos) {
      this.pos = pos
      this.vel = p.createVector()
    }

    follow(mouse) {
      let dir = this.pos.sub(mouse)
      this.vel.add(dir)
    }

    draw() {
      this.pos.add(this.vel)
      p.ellipse(this.pos.x, this.pos.y, 10)
    }
  }
}
