import { createGlobalStyle } from 'styled-components'

export const GlobalStyles = createGlobalStyle`
  body {
    background: ${props => props.theme.base};
    transition: background .5s ease;
    overflow: hidden
  }

  * {
    margin: 0;
    padding: 0;
    font-family: 'Nunito';
    font-size: 1rem;
    box-sizing: border-box;
  }

  @keyframes pulse {
    0% { box-shadow: 0 0 0 0 rgba(255, 255, 255, .15) }
    100% { box-shadow: 0 0 0 60px rgba(255, 255, 255, 0) }
  }
`

const baseStyles = {
  primary: '#60b389',
  accent: '#97cfb3',

  weight: {
    thin: '100',
    regular: '400',
    bold: '700'
  }
}

export const theme = {
  dark: {
    mode: 'dark',
    base: '#111111',
    complement: '#eeeeee',
    ...baseStyles
  },

  light: {
    mode: 'light',
    base: '#eeeeee',
    complement: '#111111',
    ...baseStyles
  }
}
