import React from 'react'
import { makeStyles } from '@material-ui/styles'
import P5Wrapper from 'react-p5-wrapper'

import ThemeToggler from './themeToggler'
import TwoFace from './twoFace'
import follower from './follower'
import Text from './text'

const useStyles = makeStyles( theme => ({
  twoFace: {
    // display: 'flex',
    // justifyContent: 'center',
    // marginTop: '2rem',
    // border: `10px solid ${theme.accent}`
  },

  toggler: {
    marginTop: '1rem'
  },

  // wd: {

  // },

  // gd: {

  // },

  yinYang: {
    display: 'flex',
    top: 150,
    width: '100%',
    position: 'absolute',
    justifyContent: 'space-around'
  }
}))

export default function BenWeber (props) {
  const classes = useStyles(props.theme)

  return (
    <>
      <ThemeToggler toggleMode={props.toggleMode} className={classes.toggler} />
      <div className={classes.yinYang}>
      <Text size={3} type='h1' color='#111' className={`${classes.wd} ${classes.both}`}>Wed Developer</Text>
      <Text size={3} type='h1' color='#eee' className={`${classes.gd} ${classes.both}`}>Graphic Designer</Text>
      </div>

      <TwoFace className={classes.twoFace} />
      <Text type='h1'>I am supposed to be an h1</Text>
      <Text type='h2'>I am supposed to be an h2</Text>
      <Text type='h3'>I am supposed to be an h3</Text>
      <Text type='p'>I am supposed to be a p. For the sake of testing, I am going to make this paragraph much longer than the rest. The reason for this is simple: when you are putting a paragraph tag into your webpage, it is imoplied that the amount of text inside of that tag is going to vastly outnumber the amount of words in say an h2 or h2 tag. Anyways, I hope this text is long enough to at least go onto the next line so that I can better see what this thin is going to look lie when I finally publish my site</Text>

      {/* TODO: I think you need to use the withTheme higher orer component so all compnents have access to the theme */}
      {/* <P5Wrapper sketch={follower} theme={props.theme} /> */}
      {/* TODO: fix bug where pic has no height when real is shown. Should be a simple fix */}
    </>
  )
}
