import React from 'react'
import ReactDOM from 'react-dom'
import { styled, ThemeProvider } from 'styled-components'

import FuckyButton from './fuckyButton'
import theme from './theme'

const darkMode = theme.dark
const lightMode = theme.light

// const localStyles = theme => {
//   background: theme.
// }

const LittleComponent = styled.div`
  width: 200px
  height: 50px
  border-radius: 25px
  background: palevioletred
  border: 5px solid white
`

class BenWeber extends React.Component {
  state = {
    theme: darkMode
  }

  toggleMode = () => {
    this.state.theme === darkMode ? this.setState({ theme: lightMode }) 
    : this.setState({ theme: darkMode })
  }

  render() {
    return (
      <ThemeProvider theme={this.state.theme[this.state.mode]}>
        <LittleComponent />
      </ThemeProvider>
    )
  }  
}

//use withStyles
const rootElement = document.getElementById('root')
ReactDOM.render()
