import React from 'react'
import styled from 'styled-components'

import Text from './Text'

const Gallery = styled.div`
  display: grid;
  grid-template-areas: 'mesh matrix star';
  grid-template-columns: repeat(3, 1fr);
  grid-gap: .5rem;

  @media only screen and (max-width: 1000px) {
    grid-template-areas: 'mesh' 'matrix' 'star';
  }
`

const Project = styled.div`
  background-image: url('../img/${props => props.preview}');
  background-size: cover;
  grid-area: ${props => props.id};
  height: 400px;
  cursor: pointer;

  &:hover { .overlay { opacity: 1 } }

  .overlay {
    width: 100%;
    height: 100%;
    padding: 2rem;
    background: #0008;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    color: #fff;
    text-align: center;
    opacity: 0;
    transition: opacity .5s ease;
    color: #fff;
  }
`

const projectData = [
  { id: 'avole', description: 'what the fuck did ou just say about me you little fuckin gbotch ill have you know that i a  afag', img: 'avole.png' },
  { id: 'airpact', description: 'asdfasfasdf', img: 'airpact-fire.png' },
  { id: 'vizinet', description: 'asdfasdfasdf', img: 'vizinet.png' },
]

const projects = projectData.map((proj, i) => {
  return (
    <Project gridArea={proj.id} preview={proj.img} key={i}>
      <div className='overlay'>
        <p>{proj.description}</p>
      </div>
    </Project>
  )
})

export default function Programming(props) {
  return (
    <Gallery>
      {projects}
    </Gallery>
  )
}
