import React from 'react'
import ReactDOM from 'react-dom'

import { ThemeProvider, createGlobalStyle } from 'styled-components'

import { theme } from './theme'
import BenWeber from './benWeber'

const darkMode = theme.dark
const lightMode = theme.light

const GlobalStyle = createGlobalStyle`
  @import url('https://fonts.googleapis.com/css?family=Heebo:100,400,700&display=swap');

  body {
    background: ${props => props.theme.base}
  }

  * {
    font-family: 'Heebo';
    transition: background .5s ease;
  }
`

class App extends React.Component {
  state = {
    theme: darkMode
  }

  toggleMode = () => {
    this.state.theme === darkMode ? this.setState({ theme: lightMode }) 
    : this.setState({ theme: darkMode })
  }

  render() {
    return (
      <ThemeProvider theme={this.state.theme}>
        <GlobalStyle />
        <BenWeber theme={this.state.theme} toggleMode={this.toggleMode} />
      </ThemeProvider>
    )
  }  
}

ReactDOM.render(
  <App />,
  document.getElementById('root')
)
