class Particle {
  constructor(p) {
    this.respawn();
    this.pos = p.createVector(p.random(-100, width+100), p.random(-100, height+100));
    this.p
  }

  move() {
    this.pos.add(this.vel);
    this.checkPos();
  }

  draw() {
    this.p.strokeWeight(this.size);
    this.p.stroke(255);
    this.p.point(this.pos.x, this.pos.y);
  }

  applyForce(force) {
    let dist = force.mag();
    let strength = .3 / Math.pow(dist, 2);
    strength = p.constrain(strength, 0, .0005 );
    force.setMag(strength);
    this.vel.add(force);
  }

  checkPos() {
    let x = this.pos.x;
    let y = this.pos.y;
    let s = 100;

    if ((x < 0 - s) ||
        (x > width + s) ||
        (y < 0 - s) ||
        (y > height + s))
    {
        this.respawn();
    }
  }

  respawn() {
    let rate = this.p.random(.2, .4);
    this.pos = this.p.createVector(random(-100, width + 100), -100);
    this.vel = this.p.createVector(random(-rate, rate), this.p.random(-rate + rate, rate));
    this.size = this.p.random(1, 10);
  }
}
