export const theme = {
  dark: {
    mode: 'dark',

    base: '#111111',
    complement: '#eeeeee',

    primary: '#60b389',
    accent: '#97cfb3',

    weight: {
      thin: '100',
      regular: '400',
      bold: '700'
    },

    fontSize: {
      h1: '2rem',
      h2: '1.5rem',
      h3: '1.25rem',
      p: '10rem'
    }
  },

  // factor these out then export a concatenated dictionary

  light: {
    mode: 'light',

    base: '#eeeeee',
    complement: '#111111',

    primary: '#60b389',
    accent: '#97cfb3',

    weight: {
      thin: '100',
      regular: '400',
      bold: '700'
    },

    fontSize: {
      h1: '2rem',
      h2: '1.5rem',
      h3: '1.25rem',
      p: '10rem'
    }
  }
}
