import React from 'react'
import styled from 'styled-components'

const ThemeToggler_ = styled.button`
  padding: .5rem 2rem;
  // font-weight: ${props => props.theme.weight.light};
  font-weight: 100;
  border: 0;
  border-radius: 100px;
  outline: 0;
  margin: 0 1rem;

  background: ${props => props.theme.complement};
  color: ${props => props.theme.base}
`

export default function ThemeToggler (props) {
  console.log(props)
  return (
    <div style={{
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    }}>
      <p style={{fontWeight: 100, color: props => props.theme.base}}>Light</p>
      <ThemeToggler_ onClick={props.toggleMode} className={props.className}>What the shit</ThemeToggler_>
      <p style={{fontWeight: 700, color: props => props.theme.complement}}>Dark</p>
    </div>
  )
}
