import React from 'react'
import ReactDOM from 'react-dom'
import styled, { createGlobalStyle, ThemeProvider } from 'styled-components'

// import FuckyButton from './fuckyButton'
import { theme } from './theme'
console.log(theme)
const darkMode = theme.dark
const lightMode = theme.light

// const localStyles = theme => {
//   background: theme.
// }
// const LittleComponent = styled.div.attrs(props => ({
//   // onClick: props.onClick,
//   onCLick: () => alert('nigga')
// }))`
//   width: 200px;
//   height: 50px;
//   border-radius: 25px;
//   background: ${props => props.theme.primary};
//   border: 5px solid white;
//   color: ${props => props.theme.background}
// `

const GlobalStyle = createGlobalStyle`
  body {
    background-color: ${props => props.theme.base};
  }

  button {
    width: 200px;
    height: 50px;
    border-radius: 25px;
    border: 0;
    outline: 0;
    background: ${props => props.theme.primary};
    color: ${props => props.theme.accent};
    font-size: 1rem;
    font-weight: bold;
    text-transform: uppercase;
  }

  * {
    margin: 0;
    padding: 0;
  }
`

class BenWeber extends React.Component {
  state = {
    theme: darkMode
  }

  toggleMode = () => {
    this.state.theme === darkMode ? this.setState({ theme: lightMode }) 
    : this.setState({ theme: darkMode })
  }

  render() {
    console.log(this.state)
    return (

      <ThemeProvider theme={this.state.theme}>
        <GlobalStyle />
        {/* <LittleComponent onCLick={this.toggleMode}> */}
        <button onCLick={() => alert('asfsd')}>
          Primary
        </button>
        {/* </LittleComponent> */}
      </ThemeProvider>
    )
  }  
}

//use withStyles
ReactDOM.render(<BenWeber />, document.getElementById('root'))
