import React from 'react'
import styled from 'styled-components'

const ThemeToggler_ = styled.button`
  padding: .5rem 2rem;

  border-radius: 100px;
  border: 0;
  outline: 0;

  background: ${props => props.theme.complement};
  color: ${props => props.theme.base}
`

export default function ThemeToggler (props) {
  return (
    <div style={{
      display: 'flex',
      alignItems: 'center',
      // justifyContent: 'center',
    }}>
      <p>Light</p>
      <ThemeToggler_ onClick={props.toggleMode} className={props.className} />
      <p>Dark</p>
    </div>
  )
}
