import React from 'react'
import ReactDOM from 'react-dom'
import { styled, ThemeProvider } from 'styled-components'

import FuckyButton from './fuckyButton'
import theme from './theme'

const darkMode = theme.dark
const lightMode = theme.light

// const localStyles = theme => {
//   background: theme.
// }

const littleComponent = stlyed.div`
  width: 200px
  height: 50px
  border-radius: 25px

`

class BenWeber extends React.Component {
  state = {
    theme: darkMode
  }

  toggleMode = () => {
    this.state.theme === darkMode ? this.setState({ theme: lightMode }) 
    : this.setState({ theme: darkMode })
  }

  render() {
    return (
      <ThemeProvider theme={this.state.theme[this.state.mode]}>
        <div style={{
          width: '500px',
          height: '300px',
          background: this.state.color
        }}>
          <FuckyButton onClick={this.fuck}>Click me in the fucking ass please thanks</FuckyButton>
        </div>
      </ThemeProvider>
    )
  }  
}

//use withStyles
const rootElement = document.getElementById('root')
ReactDom.render()
