export default function sketch(p) {
  let canvas  
  let grid = []
  let tileSize

  p.setup = () => {
    canvas = p.createCanvas(p.windowWidth, p.windowHeight)
    p.background('#0d1e35')
    tileSize = p.width / 50

    for (let x = 0; x < p.width; x += tileSize) {
      let row = []
      for (let y = 0; y < p.height; y += tileSize) {
        row.push(new Cell(p, p.createVector(x, y), tileSize))
      }
      grid.push(row)
    }
  }

  p.draw = () => {
    p.clear()
    p.background('#0d1e35')

    grid.forEach(cell => {
      cell.draw()
    })

  p.myCustomRedrawAccordingToNewPropsHandler = (newProps) => {
    if (canvas) {
      p.fill(newProps.color)
    }
  }
}

class Cell {
  constructor(p, pos, tileSize) {
    this.p = p
    this.pos = pos
    this.tileSize = tileSize
  }

  draw() {
    this.p.fill(this.p.random(255), this.p.random(255), this.p.random(255))
    this.p.rect(this.pos.x, this.pos.y, tileSize)
  }
}
}
